<?phpif( !defined("BENGINE") ) { die ("Hacking!"); }include_once(ROOT_DIR."/plugins/action/functions.php");	if(isset($nodes[2]) and $nodes[2] == "add" and !isset($nodes[3])){		#Шаблон в админке	$body = "/plugins/action/action_add_edit.tpl";}elseif(isset($nodes[2]) and $nodes[2] == "edit" and isset($nodes[3]) and (int)$nodes[3] > 0){	$action_query = doquery("SELECT * FROM `action` WHERE id=".$nodes[3]." LIMIT 1");	if(dorows($action_query) > 0) {		$content = doassoc($action_query);				if($content["nl2br"] == 1) {			$content["text"] = str_replace("<br />","",$content["text"]);		}	}	#Шаблон в админке	$body = "/plugins/action/action_add_edit.tpl";}else{	$action_query = doquery("SELECT * FROM `action` ORDER BY id");	if(dorows($action_query) > 0) {		$content = doarray($action_query);	}	$body = "/plugins/action/action.tpl";}?>